import React, { useState } from 'react';
import { useMutation } from '@apollo/react-hooks';
import { gql } from 'apollo-boost';
import styled from 'styled-components';

const AddForm = styled.div`
	display: flex;
	flex-direction: column;
`;

const SET_DRIVERS = gql`
	mutation addDriver($name: String, $age: Int, $country: String, $team: String) {
		addDriver(name: $name, age: $age, country: $country, team: $team) {
			name
			age
			country
			team
		}
	}
`;

const AddDriver = () => {
	const [ driver, setDriver ] = useState([
		{
			name: '',
			age: '',
			country: '',
			team: ''
		}
	]);

	const [ addDriver, { error } ] = useMutation(SET_DRIVERS, {
		refetchQueries: [ 'drivers' ]
	});

	if (error) console.log(`Error: ${error}`);

	return (
		<div>
			<p>Add Driver Here:</p>
			<AddForm>
				<label htmlFor="name">
					Name:
					<input type="text" name="name" />
				</label>
				<label htmlFor="age">
					Age:
					<input type="text" name="age" />
				</label>
				<label htmlFor="country">
					Country:
					<input type="text" name="country" />
				</label>
				<label htmlFor="team">
					Team:
					<input type="text" name="team" />
				</label>
			</AddForm>
		</div>
	);
};

export default AddDriver;
