import React, { useState } from 'react'
import { useMutation } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'
import styled from 'styled-components'

const AddForm = styled.div`
  display: flex;
  flex-direction: column;
`

const SET_DRIVERS = gql`
  mutation {
    addDriver(name: String, age: Int, country: String, team: String) {
      name
      age
      country
      team
    }
  }
`

const AddDriver = () => {
  let name
  let age
  let country
  let team
  const [addDriver, { data }] = useMutation(SET_DRIVERS)

  return (
    <div>
      <p>Add Driver Here:</p>
      <AddForm
        onSubmit={e => {
          e.preventDefault()
          addDriver({
            variables: {
              name: name.value,
              age: age.value,
              country: country.value,
              team: team.value,
            },
          })
          name.value = ''
          age.value = ''
          country.value = ''
          team.value = ''
        }}
      >
        {/* NAME */}
        <label htmlFor="name">
          Name:
          <input
            name="name"
            ref={node => {
              name = node
            }}
          />
        </label>
        {/* AGE */}
        <label htmlFor="age">
          Age:
          <input
            name="age"
            ref={node => {
              age = node
            }}
          />
        </label>
        {/* COUNTRY */}
        <label htmlFor="country">
          Country:
          <input
            name="country"
            ref={node => {
              country = node
            }}
          />
        </label>
        {/* TEAM */}
        <label htmlFor="team">
          Team:
          <input
            name="team"
            ref={node => {
              team = node
            }}
          />
        </label>
        <button type="submit">Add Driver</button>
      </AddForm>
    </div>
  )
}

export default AddDriver
