import React from 'react'
import PropTypes from 'prop-types'
import { useMutation } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'

const SET_DRIVERS = gql`
  mutation AddDriver(
    $name: String
    $age: Int
    $country: String
    $team: String
    $racesWon: [String]
  ) {
    addDriver(
      name: $name
      age: $age
      country: $country
      team: $team
      racesWon: $racesWon
    ) {
      name
      age
      country
      team
      racesWon
    }
  }
`

const AddDriver = () => {
  let name
  let age
  let country
  let team
  let racesWon
  const [setDrivers, { data }] = useMutation(SET_DRIVERS)

  return (
    <div>
      <p>Add Driver Here:</p>
      <form
        onSubmit={e => {
          e.preventDefault()
          addDriver()
        }}
      >
        <input
          ref={node => {
            input = node
          }}
        />
      </form>
    </div>
  )
}

export default AddDriver

AddDriver.propTypes = {
  name: PropTypes.string,
  age: PropTypes.number,
}
