import React, { useState } from 'react'
import { useMutation } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'
import styled from 'styled-components'

const AddForm = styled.div`
  display: flex;
  flex-direction: column;
`

const SET_DRIVERS = gql`
  mutation addDriver(
    $name: String
    $age: Int
    $country: String
    $team: String
  ) {
    addDriver(name: $name, age: $age, country: $country, team: $team) {
      name
      age
      country
      team
    }
  }
`

const AddDriver = () => {
  const [driver, setDriver] = useState({
    name: '',
    age: '',
    country: '',
    team: '',
  })

  const [addDriver, { error }] = useMutation(SET_DRIVERS, {
    // variables: { name, age, country, team },
    refetchQueries: ['drivers'],
  })

  if (error) console.log(`Error: ${error}`)

  const handleChange = e => {
    setDriver(([e.target.name]: e.target.value))
  }

  const handleSubmit = e => {
    e.preventDefault()
    console.log(driver)
  }

  return (
    <div>
      <p>Add Driver Here:</p>
      <AddForm onSubmit={handleSubmit}>
        <label htmlFor="name">
          Name:
          <input
            type="text"
            name="name"
            value={driver.name}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="age">
          Age:
          <input
            type="text"
            name="age"
            value={driver.age}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="country">
          Country:
          <input
            type="text"
            name="country"
            value={driver.country}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="team">
          Team:
          <input
            type="text"
            name="team"
            value={driver.team}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Add</button>
      </AddForm>
    </div>
  )
}

export default AddDriver
