import React from 'react';
import { useMutation } from '@apollo/react-hooks';
import { gql } from 'apollo-boost';

const SET_DRIVERS = gql`
	mutation AddDriver($name: String, $age: Int, $country: String, $team: String, $racesWon: [String]) {
		addDriver(name: $name, age: $age, country: $country, team: $team, racesWon: $racesWon) {
			name
			age
			country
			team
			racesWon
		}
	}
`;

const AddDriver = () => {
	let name;
	let age;
	let country;
	let team;
	let racesWon;
	const [ addDriver, { data } ] = useMutation(SET_DRIVERS);

	return (
		<div>
			<p>Add Driver Here:</p>
			<form
				onSubmit={(e) => {
					e.preventDefault();
					addDriver({
						variables: {
							name: name.value,
							age: age.value,
							country: country.value,
							team: team.value,
							racesWon: racesWon.value
						}
					});
					name.value = '';
					age.value = '';
					country.value = '';
					team.value = '';
					racesWon.value = '';
				}}
			>
				<input
					ref={(node) => {
						name = node;
					}}
				/>
				<input
					ref={(node) => {
						age = node;
					}}
				/>
				<input
					ref={(node) => {
						country = node;
					}}
				/>
				<input
					ref={(node) => {
						team = node;
					}}
				/>
				<input
					ref={(node) => {
						racesWon = node;
					}}
				/>
			</form>
		</div>
	);
};

export default AddDriver;
