import React from 'react'
import PropTypes from 'prop-types'
import { useMutation } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'

const SET_DRIVERS = gql`
  mutation {
    addDriver(
      name: String
      age: Int
      country: String
      team: String
      racesWon: [String]
    ) {
      name
      age
      country
      team
      racesWon
    }
  }
`

const AddDriver = ({ name, age }) => {
  const [setDrivers, { loading }] = useMutation(SET_DRIVERS, {
    variables: { name, age },
  })

  return (
    <div>
      <p>Add Driver Here:</p>
    </div>
  )
}

export default AddDriver

AddDriver.propTypes = {
  name: PropTypes.string,
  age: PropTypes.number,
}
