import React from 'react'
import { useMutation } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'
import styled from 'styled-components'

const AddForm = styled.div`
  display: flex;
  flex-direction: column;
`

const SET_DRIVERS = gql`
  mutation AddDriver(
    $name: String
    $age: Int
    $country: String
    $team: String
    $racesWon: [String]
  ) {
    addDriver(
      name: $name
      age: $age
      country: $country
      team: $team
      racesWon: $racesWon
    ) {
      name
      age
      country
      team
      racesWon
    }
  }
`

const AddDriver = () => {
  let name
  let age
  let country
  let team
  let racesWon
  const [addDriver, { data }] = useMutation(SET_DRIVERS)

  return (
    <div>
      <p>Add Driver Here:</p>
      <AddForm
        onSubmit={e => {
          e.preventDefault()
          addDriver({
            variables: {
              name: name.value,
              age: age.value,
              country: country.value,
              team: team.value,
              racesWon: racesWon.value,
            },
          })
          name.value = ''
          age.value = ''
          country.value = ''
          team.value = ''
          racesWon.value = ''
        }}
      >
        {/* NAME */}
        <label htmlFor="name">
          Name:
          <input
            name="name"
            ref={node => {
              name = node
            }}
          />
        </label>
        {/* AGE */}
        <label htmlFor="age">
          Age:
          <input
            name="age"
            ref={node => {
              age = node
            }}
          />
        </label>
        {/* COUNTRY */}
        <label htmlFor="country">
          Country:
          <input
            name="country"
            ref={node => {
              country = node
            }}
          />
        </label>
        {/* TEAM */}
        <label htmlFor="team">
          Team:
          <input
            name="team"
            ref={node => {
              team = node
            }}
          />
        </label>
        {/* RACES WON */}
        <label htmlFor="races-won">
          Races Won:
          <input
            name="races-won"
            ref={node => {
              racesWon = node
            }}
          />
        </label>
        <button type="submit">Add Driver</button>
      </AddForm>
    </div>
  )
}

export default AddDriver
